package com.live.ffmpeg.camera;import android.content.Context;import android.util.DisplayMetrics;public class PinMuUtils {    /**    * 获取手机屏幕宽度    * */    public static  int getwidth(Context context){        DisplayMetrics metric =context.getResources().getDisplayMetrics();        return  metric.widthPixels;    }    /**    * 获取手机屏幕高度    * */    public static  int getheight(Context context){        DisplayMetrics metric =context.getResources().getDisplayMetrics();        return metric.heightPixels;    }    /**    * px转dp    * */    public static  float px2dp(Context context , int px){        DisplayMetrics metric =context.getResources().getDisplayMetrics();        return  px / metric.density+0.5f;    }    /**    * dp转转px    * */    public static float dp2px(Context context , float dp){        DisplayMetrics metric =context.getResources().getDisplayMetrics();        return  dp*metric.density+0.5f;    }    /**    * sp 转px    * */    public static int sp2px(Context context, float spValue) {        final float fontScale = context.getResources().getDisplayMetrics().scaledDensity;        return (int) (spValue * fontScale + 0.5f);    }    /**    * px转sp    * */    public static int px2sp(Context context, float pxValue) {        final float fontScale = context.getResources().getDisplayMetrics().scaledDensity;        return (int) (pxValue / fontScale + 0.5f);    }}